<?php

namespace EventBundle\Repository;

/**
 * PostRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PostRepository extends \Doctrine\ORM\EntityRepository
{
    public function getPostByTitle($title)
    {
        return $this
        ->createQueryBuilder('p')
        ->where('p.title = :title')
        ->SetParameter('title',$title)
        ->getQuery()
        ->getResult();
    }
    public function findThreeLastPosts()
    {
        return $this->createQueryBuilder('p')
            ->orderBy('p.publishedAt', 'DESC')
            ->getQuery()
            ->setMaxResults(3)
            ->getResult();
    }
    public function getFiveRow()
    {
        return $this->createQueryBuilder('p')
            ->limit(5,5)
            ->getQuery()
            ->getResult();
    }

    public function getByResearch($search)
    {
        // CrÃ©ation du QueryBuilder pour rechercher un mot dans le contenu ou le titre des artiles
        return $this->createQueryBuilder('p')
            ->where('p.title LIKE :search')
            ->orWhere('p.content LIKE :search')
            ->setParameter('search', '%' .$search. '%')
            ->getQuery()
            ->getResult();
    }
 
}
